/*Table structure for table `block_pos` */

DROP TABLE IF EXISTS `block_pos`;

CREATE TABLE `block_pos` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `blocks_id` int(10) NOT NULL,
  `position` int(10) DEFAULT '0',
  `id_column` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `block_pos` */

/*Table structure for table `block_skin` */

DROP TABLE IF EXISTS `block_skin`;

CREATE TABLE `block_skin` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `open` longtext COLLATE utf8_unicode_ci NOT NULL,
  `close` longtext COLLATE utf8_unicode_ci,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `block_skin` */

insert  into `block_skin`(`id`,`title`,`open`,`close`) values (1,'logo-banner','<div style=\"margin-top:1px;\">','</div>'),(2,'blue_block','<div class=\"blue_block\">','</div>'),(3,'no border','<div style=\"float:left; clear:both; display:bock;margin-bottom:4px\">','</div>'),(4,'blue_border','<div class=\"blue_border\">','</div>');

/*Table structure for table `blocks` */

DROP TABLE IF EXISTS `blocks`;

CREATE TABLE `blocks` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `skin` int(10) DEFAULT NULL,
  `mota` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `type` enum('nhomtin','bantin','html','survey','video','thongke') COLLATE utf8_unicode_ci NOT NULL DEFAULT 'nhomtin',
  `meta` text COLLATE utf8_unicode_ci,
  `id_content` char(20) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'A',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `blocks` */

/*Table structure for table `captcha` */

DROP TABLE IF EXISTS `captcha`;

CREATE TABLE `captcha` (
  `captcha_id` bigint(13) unsigned NOT NULL AUTO_INCREMENT,
  `captcha_time` int(10) unsigned DEFAULT NULL,
  `ip_address` varchar(16) COLLATE utf8_unicode_ci DEFAULT '0',
  `word` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`captcha_id`),
  KEY `word` (`word`)
) ENGINE=MyISAM AUTO_INCREMENT=317537 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

