/*Table structure for table `crawler` */

DROP TABLE IF EXISTS `crawler`;

CREATE TABLE `crawler` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `id_nwc` int(11) NOT NULL,
  `id_cattext` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `id_pattern` int(11) NOT NULL,
  `links` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `create_time` int(11) DEFAULT NULL,
  `update_time` int(11) DEFAULT NULL,
  `is_video` enum('0','1') COLLATE utf8_unicode_ci DEFAULT '0',
  `available` enum('1','0') COLLATE utf8_unicode_ci NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=267 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

