/*Table structure for table `function_permits` */

DROP TABLE IF EXISTS `function_permits`;

CREATE TABLE `function_permits` (
  `id` int(6) NOT NULL AUTO_INCREMENT,
  `id_class` int(6) NOT NULL,
  `func_name` varchar(30) COLLATE utf8_unicode_ci NOT NULL,
  `required_access` int(11) NOT NULL,
  `note` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
