/*Table structure for table `news` */

DROP TABLE IF EXISTS `news`;

CREATE TABLE `news` (
  `id_news` int(11) NOT NULL AUTO_INCREMENT,
  `id_nwc` int(11) NOT NULL,
  `id_text` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `id_cattext` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_quickview` varchar(1000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_content` text COLLATE utf8_unicode_ci,
  `news_author` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_link` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_tag` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_viewcount` int(11) DEFAULT '1',
  `news_img` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_img_thumb` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_video` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `news_video_meta` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `news_meta` varchar(1000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `news_special` enum('0','1') COLLATE utf8_unicode_ci DEFAULT '0',
  `news_special_create_date` int(11) DEFAULT NULL,
  `news_specialtime` int(11) DEFAULT NULL,
  `news_lead` enum('0','1') COLLATE utf8_unicode_ci DEFAULT '0',
  `news_lead_create_date` int(11) DEFAULT NULL,
  `news_useful` enum('0','1') COLLATE utf8_unicode_ci DEFAULT '0',
  `news_useful_create_date` int(11) DEFAULT NULL,
  `news_audit` enum('0','1') COLLATE utf8_unicode_ci DEFAULT '0',
  `news_show` enum('0','1') COLLATE utf8_unicode_ci DEFAULT '1',
  `news_delete` int(2) NOT NULL DEFAULT '0',
  `news_date` int(11) DEFAULT NULL,
  PRIMARY KEY (`id_news`),
  KEY `ss_news_FKIndex2` (`id_nwc`)
) ENGINE=MyISAM AUTO_INCREMENT=101 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `news` */

insert  into `news`(`id_news`,`id_nwc`,`id_text`,`id_cattext`,`news_title`,`news_quickview`,`news_content`,`news_author`,`news_link`,`news_tag`,`news_viewcount`,`news_img`,`news_img_thumb`,`news_video`,`news_video_meta`,`news_meta`,`news_special`,`news_special_create_date`,`news_specialtime`,`news_lead`,`news_lead_create_date`,`news_useful`,`news_useful_create_date`,`news_audit`,`news_show`,`news_delete`,`news_date`) values (1,24,'De-duoc-la-chinh-minh','Hon-nhan-gia-dinh','Để được là chính mình ','PN - Bức xúc từ một số lượng lớn thư gửi về cho chị Hạnh Dung có chung một nỗi niềm: cuộc sống hôn nhân đầy bất trắc nhưng các chị không dám ly hôn vì e sợ nhiều khó khăn trở ngại ở phía trước, Báo Phụ Nữ đã tổ chức buổi tọa đàm Bước qua đổ vỡ ngày 10/10/2010 để bạn đọc có dịp chia sẻ, trao đổi kinh nghiệm giải quyết những vấn đề “hậu ly hôn”.','<p><span style=\"font-size: 13px;\">Sau buổi tọa đàm, Báo Phụ Nữ tiếp tục mở ra diễn đàn này và sau hai tháng đã nhận được 289 bài viết của bạn đọc tham gia, phần lớn là chị em phụ nữ. </span></p>\n<p><strong><span style=\"font-size: 13px;\">Một mình giữa bộn bề&#160; </span></strong></p>\n<p><span style=\"font-size: 13px;\">Mỗi người một cảnh. Không ít chị mới ly hôn vài tháng, còn đang đối diện với ngổn ngang trăm mối tơ vò, nhưng cũng có nhiều chị ly hôn đã lâu mà bước mãi vẫn… chưa qua, vì trong lòng còn cất giữ một mối hận bị phụ bạc, ruồng bỏ. Có đọc từng bài viết của các chị, mới cảm nhận hết được những nỗi khó khăn chồng chất mà mỗi người phụ nữ phải đối diện sau biến cố trọng đại đó. Không chỉ đơn giản là nỗi cô đơn vì sự thiếu vắng người đàn ông&#160; trụ cột của gia đình, mà còn là mặc cảm của một người mẹ thất bại đã không giữ được cha cho con, không níu được bước chân người chồng; sự sợ hãi khi con đường phía trước không có ai tiếp sức với mình trong việc nuôi dạy con… Rồi còn những định kiến của miệng đời, xem người đã ly hôn như là kẻ vô dụng: “Chồng còn giữ không nổi, nói ai nghe”, như lời tâm sự của cô giáo Thủy ở huyện Mỏ Cày Nam, tỉnh Bến Tre. Có chị đã chao đảo đến nỗi chỉ muốn tìm đến cái chết (như chị Hà Minh), có chị rơi vào trạng thái trầm cảm một thời gian dài (chị Nhật Hạ – Q.Bình Thạnh).</span></p>\n<p>\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n    <tbody>\n        <tr>\n            <td><span style=\"font-size: 13px;\"><img src=\"http://crawl.smartdzine.vnuploads/news/12_2010/1291966926_83_0.jpg\" alt=\"\" /></span></td>\n        </tr>\n    </tbody>\n</table>\n</p>\n<p style=\"text-align: center;\"><em><span style=\"font-size: 13px;\">Ảnh: Gettyimages.com</span></em></p>\n<p><span style=\"font-size: 13px;\">Tuy chỉ chiếm một số ít trong lượng ý kiến phản hồi (21/289 ý kiến), nhưng những bày tỏ của các anh cũng cho thấy, cánh đàn ông khi đối mặt với đổ vỡ còn nặng nề và kinh khủng hơn phụ nữ. Mang tiếng là phái mạnh, họ không thể kể lể với bạn bè, người thân, chỉ biết dùng rượu để giải khuây. “Cảm giác như mình vừa bị bổ một cú thật mạnh vào đầu, khiến nhiều ngày sau cứ ở trong trạng thái lơ lửng giữa khoảng không. Cũng đi, cũng đứng, cũng ăn, cũng nói nhưng chẳng hiểu, chẳng nhớ được mình đã đi đâu, nói gì, ăn gì…” (anh Trọng Khôi, Q.Thủ Đức).</span></p>\n<p><strong><span style=\"font-size: 13px;\">Dọn lại quá khứ&#160; </span></strong></p>\n<p><span style=\"font-size: 13px;\">Con cái là động lực lớn nhất được hầu hết các anh chị tựa vào trong hành trình tự đứng lên sau cú sốc tâm lý đó. Các chị Quế Anh (Q.4), Nguyễn Thị Trà (H.Củ Chi), Phi Diệp Thu (Q.Tân Bình) cùng rất nhiều chị khác đã lần hồi tạo dựng lại cuộc sống mới, không từ nan bất cứ công việc lương thiện nào miễn sao có thu nhập đủ sống mà nuôi con.</span></p>\n<p><span style=\"font-size: 13px;\">“Ly hôn là lựa chọn đúng trong một thời điểm nào đó của cuộc đời, nhưng tiếp tục sống với lựa chọn đúng ấy chẳng dễ dàng gì”. Đúc kết của chị Nguyễn Thị Trà (H.Củ Chi) sau nhiều năm một mình nuôi con&#160; nhận được khá nhiều sự&#160; đồng tình. Có chị, chỉ vì không vượt qua được những khó khăn về tài chính đã chấp nhận làm vợ hờ để rồi sau khi đọc các bài viết trên diễn đàn, đã tự vấn “Sao không cố gắng hết sức để đứng được trên đôi chân của mình?” (chị Ngọc Thanh – Q.Tân Phú). </span></p>\n<p><span style=\"font-size: 13px;\">Những khó khăn về vật chất so ra cũng chỉ đứng hàng thứ nhì, việc giữ vững tâm lý cho con tiếp tục học tốt mới luôn là nỗi lo hàng đầu của mỗi người mẹ. Nhiều chị phải cố gắng dẹp bỏ tự ái để con giữ được mối liên hệ với cha, điều mà không phải ai cũng làm được. Có chị sau này, khi con gái đã 20 tuổi, mới ân hận là mình đã sai lầm chặt đứt sợi dây tình cảm cha và con gái (chị V.M., TP.HCM).</span></p>\n<p><strong><span style=\"font-size: 13px;\">Xây dựng tương lai </span></strong></p>\n<p><span style=\"font-size: 13px;\">Nỗi buồn nào rồi cũng dần phải nhường chỗ cho niềm vui. Đấy là buổi chiều đón con ở trường, được con khoe điểm 10; những buổi tối mẹ con cùng ngồi bên mâm cơm đạm bạc nhưng êm ả. Ổn định được công việc, con cái ngoan ngoãn, học giỏi… các chị bắt đầu nghĩ đến bản thân mình. Các chị thú nhận, trước đây chưa bao giờ nghĩ sau khi ly hôn mình sẽ có được một cuộc sống bình yên như thế. “Giờ nghĩ lại không hiểu sao ngày đó mình có thể tồn tại một suy nghĩ rất nông cạn trong đầu là không có người đàn ông đó, mình chẳng còn gì để sống trong cõi đời này” (chị Ngọc Tiên, Q.12). </span></p>\n<p><span style=\"font-size: 13px;\">Thực tế, cuộc sống của các chị vẫn tiếp tục và còn khởi sắc hơn. Chị Nhật Hạ (Q.Thủ Đức) tranh thủ học đại học tại chức để nâng cao chuyên môn. Các chị Lan Anh, Cẩm Hồng… sau nhiều năm hy sinh cho con, giờ đã có thời gian để tập thể dục, học khiêu vũ, đọc sách, đi du lịch… Chị&#160; Trâm (TP. Mỹ Tho) còn bất ngờ về khả năng kinh doanh của mình. Chỉ 5 năm sau ly hôn, chị đã làm chủ một hệ thống cửa hàng trang trí nội thất. Chị thật thà nhìn nhận: “Nếu không ly hôn, chắc tôi vẫn còn là một bà nội trợ mang tiếng ăn bám chồng. Giờ thì tôi có thể trở lại với ước mơ trở thành một nhà kinh doanh ấp ủ từ thời con gái”.</span></p>\n<p><span style=\"font-size: 13px;\">Nhiều chị đơn giản hơn, chỉ ước mơ tìm được một người tốt để yêu thương và được yêu thương. Điều đó, không ít các chị, các anh đã đạt được. Chị Quế Anh (Q.4) đã&#160; kết hôn lần hai với một người đàn ông cùng cảnh ngộ ở tuổi 40. Chị Ngân Giang (Bình Dương) thì mãi đến 20 năm sau ly hôn mới đi bước nữa với bố của đứa học trò mồ côi mẹ. Từ những đau thương, mất mát đã trải qua, các chị tự tin mình sẽ giữ được mái nhà thứ hai bình yên.</span></p>\n<p><span style=\"font-size: 13px;\">Sau giông bão bầu trời luôn xanh trong và tươi đẹp hơn. Cảm ơn các chị, các anh đã nói lên điều đó qua diễn đàn này.</span></p>\n<p style=\"text-align: right;\"><strong><span style=\"font-size: 13px;\">Ban Hôn nhân - Gia đình</span></strong></p>\n<p><span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/buoc-qua-do-vo.aspx\"><span style=\"font-size: 13px;\">Bước qua đổ vỡ&#160;&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/di-mot-minh-khong-de.aspx\"><span style=\"font-size: 13px;\">Đi một mình không dễ&#160;&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/nang-long.aspx\"><span style=\"font-size: 13px;\">Nặng lòng&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt; </span><a href=\"/honnhan-giadinh/2010/Pages/chi-can-tu-tin-va-no-luc.aspx\"><span style=\"font-size: 13px;\">Chỉ cần tự tin và nỗ lực</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/toi-da-tu-thay-doi-so-phan.aspx\"><span style=\"font-size: 13px;\">Tôi đã tự thay đổi số phận</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt; </span><a href=\"/honnhan-giadinh/2010/Pages/binh-yen-sau-do-vo.aspx\"><span style=\"font-size: 13px;\">Bình yên sau đổ vỡ&#160;&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/lam-lai-cuoc-doi.aspx\"><span style=\"font-size: 13px;\">Làm lại cuộc đời&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/co-quen-de-song.aspx\"><span style=\"font-size: 13px;\">Cố quên để sống&#160;&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/hanh-phuc-sau-ly-hon.aspx\"><span style=\"font-size: 13px;\">Hạnh phúc sau ly hôn&#160;&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/phu-nu-dep-nhat-la-khi-ngang-cao-dau.aspx\"><span style=\"font-size: 13px;\">Phụ nữ đẹp nhất là khi ngẩng cao đầu</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/buoc-mot-chan.aspx\"><span style=\"font-size: 13px;\">Bước một chân</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt; </span><a href=\"/honnhan-giadinh/2010/Pages/sau-can-mua-troi-da-sang.aspx\"><span style=\"font-size: 13px;\">Sau cơn mưa trời đã sáng&#160;&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/buoc-chua-qua.aspx\"><span style=\"font-size: 13px;\">Bước... chưa qua!</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/quyét-sóng.aspx\"><span style=\"font-size: 13px;\">Quyết sống</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/sau-chien-thang-la-chien-bai.aspx\"><span style=\"font-size: 13px;\">Sau chiến thắng là... chiến bại&#160;</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/toi-van-con-mac-no.aspx\"><span style=\"font-size: 13px;\">Tôi vẫn còn mắc nợ…</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/ta-do-dung-keo-theo-nguoi-khac-vo.aspx\"><span style=\"font-size: 13px;\">Ta “đổ” đừng kéo theo người khác “vỡ”!</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/su-dut-khoat-can-thiet.aspx\"><span style=\"font-size: 13px;\">Sự dứt khoát cần thiết</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/hon-ca-ac-mong.aspx\"><span style=\"font-size: 13px;\">Hơn cả ác mộng</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/khong-dam-buoc-qua.aspx\"><span style=\"font-size: 13px;\">Không dám bước qua</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/chong-chenh-mot-loi-ve.aspx\"><span style=\"font-size: 13px;\">Chông chênh một lối về...</span></a><br />\n<span style=\"font-size: 13px;\">&gt;&gt;&#160;</span><a href=\"/honnhan-giadinh/2010/Pages/bai-toan-da-co-dap-so.aspx\"><span style=\"font-size: 13px;\">Bài toán đã có đáp số</span></a></p>','Theo Phụ nữ Online','http://www.phunuonline.com.vn/honnhan-giadinh/2010/Pages/de-duoc-la-chinh-minh.aspx',NULL,1,'uploads/news/12_2010/1291966926_83_0.jpg','uploads/news/12_2010/1291966926_83_0_thumb_thumb.jpg','','','uploads/news/12_2010/1291966926_83_0.jpg','1',1291967072,1291974272,'1',1291967072,'1',1291967072,'1','1',0,1291966925),(2,26,'Thu-gian-voi-spa-tai-nha','Lam-dep','Thư giãn với spa tại nhà ','PNO - Không nhất thiết bạn phải đến spa tìm sự thư giãn. Chỉ với một số bước chuẩn bị, bạn có thể biến phòng tắm thành spa tại nhà vào dịp cuối tuần hay những khi căng thẳng.','<p style=\"text-align: left;\"><span style=\"font-size: 13px;\"><strong>1. Tạo không gian</strong></span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">Bắt đầu tạo không gian thư giãn cho phòng tắm bằng ánh sáng. Hãy tắt đèn điện và sử dụng ánh sáng tự nhiên. Đèn điện thật sự không tốt cho mắt và khi bắt đầu một buổi spa thì bạn cũng nên để mắt được&#160;thư giãn. Nên sử dụng nến để tạo không gian dễ chịu hơn. Với 3 ngọn nến xung quanh bồn hay góc phòng, không gian sẽ dịu dàng hơn rất nhiều. Tuy nhiên bạn cần chú ý không nên sử dụng nến thơm vì nó làm biến đổi mùi hương trong phòng tắm khi bạn bắt đầu spa. Không nên sử dụng nến có mùi hoa oải hương hay gỗ đàn hương vì nó có thể khiến hạ huyết áp, đồng thời làm mất đi hương thơm của những loại sữa tắm mà bạn yêu thích.</span></p>\n<p style=\"text-align: left;\">\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n    <tbody>\n        <tr>\n            <td><img alt=\"\" src=\"http://crawl.smartdzine.vnuploads/news/12_2010/1291966937_93_0.jpg\" /></td>\n        </tr>\n    </tbody>\n</table>\n</p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\"><strong>2. Ngâm mình trong bồn tắm</strong></span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">Nên tạo thật nhiều bọt xà phòng vào một buổi spa tại nhà thế này. Thêm vài giọt tinh dầu với mùi hương yêu thích của bạn. Mua cho mình vài hoa hồng nhung để rắc những cánh hoa vào bồn tắm. Có thể massage cánh hoa hồng lên da khi bạn làm sạch cơ thể. Bắt đầu ngâm mình và thư giãn với chiếc bông tắm quen thuộc. Bật một điệu nhạc du dương. Tất nhiên bạn không nên nghe nhạc bằng headphone. Trong lúc ngâm mình, bạn có thể thưởng thức một ly rượu vang đỏ, chất oxy hóa trong rượu vang tốt cho hệ tuần hoàn và tim mạch. Cũng có thể là một ly nước trái cây không đường nếu bạn muốn đẹp da và giảm cân.</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\"><strong>3. Phục hồi và dưỡng tóc</strong></span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n    <tbody>\n        <tr>\n            <td><img alt=\"\" src=\"http://crawl.smartdzine.vnuploads/news/12_2010/1291966938_93_1.jpg\" /></td>\n        </tr>\n    </tbody>\n</table>\n</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">Hãy phục hồi sức sống cho tóc của bạn sau bao ngày bay trong “khói bụi đường phố” bằng một công thức tự nhiên. Trộn hai muỗng&#160;cà phê&#160;dầu dừa với nửa quả bơ đã xay nhuyễn, thêm vào đó ¼ chén nước cốt dừa và xoa dung dịch này lên tóc, nhẹ nhàng xoa đều. Bạn có thể kết hợp việc này với mát xa da đầu. Để hỗn hợp này trong khoảng 15 đến 20 phút sau đó xả lại bằng nước nhiều lần cho đến khi hết mùi.</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">Nếu bạn không có thời gian, có thể sử dụng những sản phẩm chăm sóc tóc tại nhà như kem hoặc dầu ủ tóc. Trong thời gian ngâm mình trong bồn tắm, có thể ủ kem lên tóc để dưỡng chất thấm vào tóc, giúp tóc mượt mà hơn.</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\"><strong>4. Đắp mặt nạ</strong></span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n    <tbody>\n        <tr>\n            <td><img alt=\"\" src=\"http://crawl.smartdzine.vnuploads/news/12_2010/1291966938_93_2.jpg\" /></td>\n        </tr>\n    </tbody>\n</table>\n</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">Gương mặt là đối tượng cần được chúng ta ưu ái chăm sóc. Một buổi spa gần gũi với tự nhiên và hạn chế tối đa hóa chất công nghiệp thì không lựa chọn nào tốt hơn một chiếc mặt nạ trái cây. Trái cây có chứa vitamin và khoáng chất giúp làm mờ các vết thâm, sẹo, những điểm sạm trên da, giúp da mịn và mềm mại. Có nhiều loại mặt nạ trái cây. Giản tiện nhất vẫn là cà chua, bơ, khoai tây, nho, dưa leo tươi… Không nên cắt lát mỏng như nhiều người vẫn thường làm, mà hãy xay nhuyễn cho vào tủ lạnh khoảng 30 phút trước khi sử dụng. Bạn cũng có thể sử dụng sản phẩm mặt nạ với các tác dụng phù hợp với da mình như mặt nạ chống nhờn, mặt nạ chống lão hóa da, mặt nạ làm ẩm da…</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\"><strong>5. Ngâm chân</strong></span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">\n<table cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\n    <tbody>\n        <tr>\n            <td><img alt=\"\" src=\"http://crawl.smartdzine.vnuploads/news/12_2010/1291966939_93_3.jpg\" /></td>\n        </tr>\n    </tbody>\n</table>\n</span></p>\n<p style=\"text-align: left;\"><span style=\"font-size: 13px;\">Bàn chân là nơi phải hoạt động và tiếp xúc với bụi bẩn nhiều nhất trên cơ thể. Bạn nên chú ý đến việc làm sạch và thư giãn cho đôi chân của mình. Việc đầu tiên là tẩy sạch màu sơn nếu bạn có vẽ móng. Chà móng chân với chanh giúp màu móng sáng hơn. Ngâm châm và kì cọ sạch đôi chân của bạn trong nước ấm vài phút. Nếu có thể, hãy cho vào nước ngâm vài giọt dầu cây trà. Massage lòng bàn chân bằng cách lăn chân trên một quả bóng tennis hoặc chà xát nhẹ lên một bảng gỗ dùng mát xa chân. Cuối cùng, rửa sạch chân bằng nước thường và lau lại bằng khăn bông khô.</span></p>\n<p style=\"text-align: right;\"><span style=\"font-size: 13px;\"><strong>Khang Du</strong></span></p>','Theo Phụ nữ Online (thời trang - làm đẹp)','http://www.phunuonline.com.vn/thoitrang-lamdep/2010/Pages/thu-gian-va-lam-dep-trong-nha-tam.aspx',NULL,1,'uploads/news/12_2010/1291966937_93_0.jpg','uploads/news/12_2010/1291966937_93_0_thumb_thumb.jpg','','','uploads/news/12_2010/1291966937_93_0.jpg~uploads/news/12_2010/1291966938_93_1.jpg~uploads/news/12_2010/1291966938_93_2.jpg~uploads/news/12_2010/1291966939_93_3.jpg','1',1291967059,1291981459,'1',1291967059,'1',1291967059,'1','1',0,1291966934),(3,2,'Thue-may-bay-cho-co-dong-vien-sang-Malaysia-Ngay-sau-khi-Viet-Nam-vao-ban-ket-hang-tram-co-dong-vien-lien-tuc-goi-dien-den-cac-hang-lu-hanh-hoi-tour-di-Malaysia-Vietravel-da-thue-2-may-bay-phuc-vu-fan-bong-da-di-va-ve-trong-ngay-1512.-Ho-Guom-ruc-lua-tron','Tin12h','Thuê máy bay chở cổ động viên sang Malaysia\r\nNgay sau khi Việt Nam vào bán kết, hàng trăm cổ động viên liên tục gọi điện đến các hãng lữ hành hỏi tour đi Malaysia. Vietravel đã thuê 2 máy bay phục vụ fan bóng đá đi và về trong ngày 15/12. > Hồ Gươm rực lử','','\r\n				<H1 class=Title>Thuê máy bay chở cổ động viên sang Malaysia</H1>\r\n<H2 class=Lead>Ngay sau khi Việt Nam vào bán kết, hàng trăm cổ động viên liên tục gọi điện đến các hãng lữ hành hỏi tour đi Malaysia. Vietravel đã thuê 2 máy bay phục vụ fan bóng đá đi và về trong ngày 15/12. <BR>> <A href=\"/GL/Xa-hoi/2010/12/3BA23FBD/\" class=Lead>Hồ Gươm rực lửa trong đêm mừng chiến thắng</A>/ <A href=\"/GL/Xa-hoi/2010/12/3BA23FA9/\" class=Lead>Đêm hội bóng đá trên đường phố</A></H2>\r\n<P class=Normal>Vietravel đã thuê 2 máy bay, tổng cộng 360 chỗ của Vietnam Airlines, khởi hành từ Hà Nội và TP HCM đi Malaysia và về trong ngày 15/12. Giá tour là 8,9 triệu đồng khởi hành từ TP HCM và 9,9 triệu đồng khởi hành từ Hà Nội. </P>\r\n<TABLE cellSpacing=0 cellPadding=3 width=385 align=center border=1>\r\n<TR>\r\n<TD>Clip <STRONG><A href=\"/GL/Xa-hoi/2010/12/3BA2404B/page_2.asp\" class=>Hồ Gươm rực lửa sau trận VN - Singapore</A></STRONG></TD></TR>\r\n<TR>\r\n<TD><EM>Clip</EM>: <STRONG><A href=\"/GL/Xa-hoi/2010/12/3BA2404B/page_3.asp\" class=>Người Sài Gòn xuống phố mừng vui</A></STRONG></TD></TR></TABLE>\r\n<P class=Normal>Ngoài ra, công ty này còn có tour đến Malaysia từ 2 đến 4 ngày, trong đó có chương trình xem bóng đá với giá hơn 9 triệu đồng. Tất cả cổ động viên tham gia tour đều được hỗ trợ băng rôn, cờ phướn, áo thun cổ động... </P>\r\n<P class=Normal>Theo ông Nguyễn Minh Mẫn, đại diện Vietravel, đơn vị muốn góp phần cổ vũ bóng đá Việt Nam trên sân vận động ở thủ đô Kuala Lumpur, giúp các cổ động viên tận mắt thưởng thức những pha bóng sôi động nhất tối 15/12. </P>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967194_1_0.jpg\" width=490 height=350 /></TD></TR>\r\n<TR>\r\n<TD class=\"Image\">Cổ vũ hết mình cho đội tuyển Việt Nam. Ảnh: <EM>Hoàng Hà.</EM></TD></TR></TABLE>\r\n<P class=Normal>Trung tâm lữ hành Hanoi Redtour cũng lên kế hoạch đón cổ động viên đến Malaysia xem đá bóng. Ông Nguyễn Công Hoan, Phó giám đốc Trung tâm cho biết, đã làm việc với Hàng hàng không quốc gia Việt Nam, đặt được 40 vé máy bay, đồng thời làm việc với đối tác tại Malaysia để đặt dịch vụ, vé máy bay. Lịch trình tour 4 ngày đến Malaysia cho khách lẻ giá trọn gói 469 USD, cộng cả vé xem bóng đá. </P>\r\n<P class=Normal>\"Sáng 9/12 đã có một đoàn gần 20 khách đăng ký tour. Số chỗ có hạn nên chúng tôi ưu tiên khách đặt sớm\", ông Hoan nói. </P>\r\n<P class=Normal>Vấn đề lo ngại của các công ty lữ hành là thiếu vé máy bay vào trước và sau ngày 15/12. Chị Tống Thu Hiền, đại diện Hanoi Tourist cho biết, mặc dù có rất nhiều khách hỏi song do thiếu vé máy bay nên công ty này chỉ nhận dịch vụ cho một đoàn 16 khách, không nhận khách lẻ. </P>\r\n<P class=Normal>Trên các diễn đàn, nhiều cổ động viên hỏi thông tin và rủ nhau đi Malaysia xem bóng đá. Anh Nguyễn Mạnh, một cổ động viên cho biết, anh và nhóm bạn đang nghiên cứu kế hoạch tự đến Malaysia xem bóng đá bởi từng đến quốc gia này vài lần. Tuy nhiên anh e ngại khó mua vé máy bay và vé bóng đá vào thời điểm cận kề. \"Chúng tôi cũng hỏi tour tại một số nơi, nếu giá cả hợp lý thì sẽ đặt tour đi về trong 2 -3 ngày là phù hợp\", anh Mạnh nói. </P>\r\n<P class=Normal>Theo ghi nhận của <EM>VnExpress.net</EM>, vé máy bay đến Malaysia dịp 15/12 đã gần hết và khá đắt đỏ. Vé lẻ từ Hà Nội đi Kualu Lumpur theo Hàng hàng không giá rẻ Air Asia có giá 7,3 triệu đồng đi ngày 14/12, về ngày 16/12. Cùng ngày đó, vé lẻ của Vietnam Airlines là gần 10 triệu đồng. </P>\r\n<TABLE cellSpacing=0 cellPadding=3 width=\"100%\" border=1>\r\n<TR>\r\n<TD class=\"Normal\"><STRONG>Vòng bán kết</STRONG> (thi đấu trên sân nhà - sân đối phương)<BR><BR>19h ngày 15/12 <STRONG>Malaysia</STRONG> -<STRONG>Việt Nam</STRONG>, sân Bukit Jalil, Kuala Lumpur<BR>19h ngày 18/12 <STRONG>Việt Nam</STRONG> - <STRONG>Malaysia, </STRONG> sân Mỹ Đình, Hà Nội</TD></TR></TABLE>\r\n<P class=Normal align=right><STRONG>Đoàn Loan</STRONG></P>\r\n			','Theo Vnexpress','http://vnexpress.net/GL/Xa-hoi/2010/12/3BA2404B/',NULL,1,'uploads/news/12_2010/1291967194_1_0.jpg',NULL,'','','uploads/news/12_2010/1291967194_1_0.jpg','0',NULL,NULL,'0',NULL,'0',NULL,'','0',1,1291967193),(4,4,'Hien-Thuc-xinh-nhu-cong-chua-Nu-ca-si-Yeu-dau-theo-gio-bay-danh-toc-xu-dien-do-yem-sac-so-hat-het-minh-trong-chuong-trinh-ca-nhac-Mua-dong-am-ap-toi-912-tai-San-khau-Ca-nhac-Lan-Anh-TP-HCM.-My-Tam-Quang-Dung-tai-hop-Du-da-co-con-gai-8-tuoi-Hien-Thuc-luon-','Van-hoa','Hiền Thục xinh như công chúa \r\nNữ ca sĩ \\\'Yêu dấu theo gió bay\\\' đánh tóc xù, diện đồ yếm sặc sỡ hát hết mình trong chương trình ca nhạc \\\'Mùa đông ấm áp\\\', tối 9/12 tại Sân khấu Ca nhạc Lan Anh, TP HCM.> Mỹ Tâm, Quang Dũng tái hợp \r\n\r\n\r\n\r\n\r\nDù đã có con ','','\r\n				<H1 class=Title>Hiền Thục xinh như công chúa </H1>\r\n<H2 class=Lead>Nữ ca sĩ \'Yêu dấu theo gió bay\' đánh tóc xù, diện đồ yếm sặc sỡ hát hết mình trong chương trình ca nhạc \'Mùa đông ấm áp\', tối 9/12 tại Sân khấu Ca nhạc Lan Anh, TP HCM.<BR>> <A href=\"/GL/Van-hoa/Am-nhac/2010/12/3BA23DB2/\" class=Lead>Mỹ Tâm, Quang Dũng tái hợp</A> </H2>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967195_2_0.jpg\" width=400 height=601 border=1 alt=\"Hiền Thục\" /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Dù đã có con gái 8 tuổi, Hiền Thục luôn giữ vẻ xinh xắn thon gọn.</TD></TR></TABLE>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967196_2_1.jpg\" width=400 height=583 border=1 /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Hiền Thục đem đến cho khán giả ca khúc \"Yêu dấu theo gió bay\" và một sáng tác mới nhất trích từ 2 album mới mà cô đang thực hiện cùng lúc.</TD></TR></TABLE>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967197_2_2.jpg\" width=400 height=574 border=1 alt=\"Hiền Thục\" /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Vừa qua, album \"Kim Ngưu\" của cô được bình chọn là album được yêu thích nhất của giải thưởng Làn Sóng Xanh.</TD></TR></TABLE>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967197_2_3.jpg\" width=400 height=631 border=1 alt=\"Mỹ Tâm\" /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Mỹ Tâm cũng đánh tóc xù xuất hiện trong đêm nhạc. Năm nay Mỹ Tâm đạt được nhiều thành công trong âm nhạc. Cô được chọn làm giám khảo của Sao Mai Điểm hẹn 2010. Cô cũng được bình chọn là nữ ca sĩ được yêu thích nhất của Làn Sóng Xanh 2010.</TD></TR></TABLE>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967198_2_4.jpg\" width=400 height=579 border=1 alt=\"Yến Trang\" /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Yến Trang sôi động trong ca khúc \"Khi nào có em\". Cô thu hút người xem bởi những màn vũ đạo bốc lửa.</TD></TR></TABLE>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967199_2_5.jpg\" width=400 height=609 border=1 alt=\"Quang Dũng\" /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Quang Dũng thể hiện nồng nàn ca khúc \"Dấu chân địa đàng\". Anh vừa trở về sau chuyến lưu diễn tại Mỹ cho dịp lễ Tạ Ơn.</TD></TR></TABLE>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967200_2_6.jpg\" width=400 height=504 border=1 alt=\"Tuấn Hưng\" /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Sau bộ phim \"<A href=\"/GL/Van-hoa/2010/10/3BA21497/\" class=Normal>Cho một tình yêu</A>\", Tuấn Hưng dần trở lại với sân khấu âm nhạc.</TD></TR></TABLE>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967200_2_7.jpg\" width=400 height=523 border=1 alt=\"Ngô Kiến Huy\" /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Ngô Kiến Huy thể hiện ca khúc \"Mùa đông ấm áp\".</TD></TR></TABLE>\r\n<TABLE cellSpacing=0 cellPadding=3 width=1 align=center border=0>\r\n<TR>\r\n<TD><IMG src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967201_2_8.jpg\" width=400 height=547 border=1 alt=\"Nam Cường (trái) và Ngân Khánh (phải)\" /></TD></TR>\r\n<TR>\r\n<TD class=\"Normal\">Nam Cường (trái) và Ngân Khánh (phải) tình tứ trong ca khúc \"Câu chuyện tình tôi\".</TD></TR></TABLE>\r\n<P class=Normal align=right><STRONG>Dung Lâm</STRONG><BR><EM>Ảnh</EM>: <STRONG>Huy Tân</STRONG></P>\r\n\r\n		<TABLE cellSpacing=0 cellPadding=0 width=100% border=0><tr><td id=tdTopic_3860_1000489192></td></tr></table>	\r\n		<script type=\"text/javascript\" language=javascript>\r\n			ShowTopicJS (3860, 5, 4, 0, \'\', 2, \'tdTopic_3860_1000489192\', 1, 1000489192);\r\n		</script>\r\n		\r\n			','Theo Vnexpress','http://vnexpress.net/GL/Van-hoa/2010/12/3BA240E8/',NULL,1,'uploads/news/12_2010/1291967195_2_0.jpg',NULL,'','','uploads/news/12_2010/1291967195_2_0.jpg~uploads/news/12_2010/1291967196_2_1.jpg~uploads/news/12_2010/1291967197_2_2.jpg~uploads/news/12_2010/1291967197_2_3.jpg~uploads/news/12_2010/1291967198_2_4.jpg~uploads/news/12_2010/1291967199_2_5.jpg~uploads/news/12_2010/1291967200_2_6.jpg~uploads/news/12_2010/1291967200_2_7.jpg~uploads/news/12_2010/1291967201_2_8.jpg','0',NULL,NULL,'0',NULL,'0',NULL,'','0',1,1291967194),(5,2,'Rung-minh-vi-quai-xe-','Tin12h','Rùng mình vì “quái xế” &nbsp;','','\r\n							<p>Trận đấu giữa đội tuyển bóng đá Việt Nam - Singapore kết thúc,  hàng ngàn người dân TPHCM, Bình Dương đổ ra đường ăn mừng chiến thắng. Nhiều thanh thiếu niên lợi dụng cơ hội này tổ chức điều khiển xe máy lạng lách, đánh võng từ đêm đến sáng, gây tai nạn và làm náo loạn đường phố.</p>\r\n<p><strong>“Bão” bất cứ lúc nào</strong><br>\r\n <br>\r\n21 giờ 50 phút ngày 8-12, đại lộ Bình Dương vắng lặng, chỉ vài bóng xe lướt qua. Sự thinh lặng bất thường như báo hiệu  trận “cuồng phong” sắp ập tới. 22 giờ, một dòng xe máy do hàng trăm thanh thiếu niên điều khiển xuất hiện để mừng đội tuyển VN chiến thắng. <br>\r\n <br>\r\n23 giờ 20 phút, một thanh niên khoảng 23 tuổi chạy trong dòng xe trên hất hàm về phía “đồng đội” nói:  “Làm một quả đi chứ!”. Thanh niên này sau đó vào số, rú ga. <br>\r\n <br>\r\nVài chiếc gần đó hưởng ứng bằng cách nẹt pô. Khoảng 10 chiếc xe tách khỏi dòng xe rẽ vào đường Phú Lợi (đường nối trung tâm thị xã Thủ Dầu Một với huyện Tân Uyên). Hai trong số 10 chiếc xe bốc đầu. </p>\r\n<p>Tiếng hú, tiếng la quá khích của vài thiếu nữ ngồi sau khiến các “quái xế” càng hăng máu xốc ga cho “ngựa sắt” lồng lộn. Đặc biệt trong tốp này có một “bóng hồng” dáng mảnh mai nhưng đua như tên bắn. “Bóng hồng” này mở màn bằng pha bứt phá, vọt lên dẫn đầu 9 “ngựa sắt”. Chỉ trong chốc lát, “dải  áp thấp” nhanh chóng mạnh lên thành “bão”. Chúng tôi cố gắng bám theo “cơn bão” nhưng chỉ sau 2 km, chúng tôi đã bị “cắt đuôi”. <br>\r\n <br>\r\n“Đội tuyển Việt Nam thắng là “bão”, thua cũng đi “bão”, ngày thường không có đá bóng cũng ... “bão”...”- anh Trần Thanh Hải, ngụ phường Phú Lợi, thị xã Thủ Dầu Một, nói. Thời gian gần đây ở Bình Dương, ban ngày thỉnh thoảng trên đại lộ Bình Dương xuất hiện vài trận “bão” nhỏ với 5-7 “quái xế”, còn ban đêm cứ khoảng từ 20-24 giờ, nhất là thứ bảy, chủ nhật, “bão” quấy loạn khu đô thị Chánh Nghĩa và TP mới Bình Dương làm người dân hãi hùng. </p>\r\n<p>\r\n<table style=\"width:238px;height:381px\" align=center>\r\n    <tbody>\r\n        <tr>\r\n            <td><img alt=\"\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967203_7_0.jpg\"></td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n            <p align=center><font color=\"#808080\"> ...và pha bốc đầu xe của “quái xế” trên đường Nguyễn Văn Cừ (quận 5 - TPHCM) vào rạng sáng 9-12. Ảnh: Xuân Thảo</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>Theo các “quái xế” ở thị xã Thủ Dầu Một, gần đây, “sân đua Bình Dương” xuất hiện ngày càng nhiều “quái xế” đến từ TPHCM. Thượng tá Phan Văn Kiết, Phó trưởng Phòng CSGT Công an tỉnh Bình Dương, cho biết lực lượng CSGT từng ghi nhận một số trường hợp thanh thiếu niên đua xe và xóa sạch hiện trường mỗi khi  tai nạn xảy ra. <br>\r\n <br>\r\nTrong số đó có các thanh thiếu niên “bão” từ TPHCM qua  Bình Dương. Theo thượng tá Kiết, gần đây, rất nhiều “quái xế” trong tỉnh bị xử phạt hơn cả chục triệu đồng, thậm chí  tịch thu xe. Tuy nhiên, bất chấp lực lượng CSGT tuần tra, xử phạt ráo riết, hiện tượng “bão” đêm vẫn lén lút diễn ra, đe dọa tính mạng người đi đường. <br>\r\n <br>\r\n<strong>“Quái xế” náo loạn đường phố </strong><br>\r\n <br>\r\nTại TPHCM, 3 giờ ngày 9-12, khi đường phố đã thông thoáng, nhiều “quái xế” bắt đầu xuất hiện. Họ đi thành từng đoàn, nẹt pô, rú ga... gây náo loạn đường phố. Các “quái xế” kéo nhau đi vòng quanh các tuyến đường Trần Hưng Đạo (quận 1), Nguyễn Tất Thành, Tôn Thất Thuyết (quận 4), Nguyễn Văn Cừ, Nguyễn Trãi (quận 5)... Tuy nhiên, mọi hành vi manh động đã bị chặn đứng bởi trên các tuyến đường này đều có lực lượng  CSGT chốt chặn.<br>\r\n <br>\r\nTrên đường Trần Hưng Đạo (đoạn phường Bến Thành, quận 1), khi các “quái xế” dàn hàng ngang chạy lạng lách, đánh võng, lập tức, lực lượng CSGT xuất hiện, cả đoàn tháo chạy, một “quái xế” ngã xuống đường, nằm bất tỉnh khá lâu mới lồm cồm bò dậy được. Ớn lạnh hơn là trên đường Nguyễn Văn Cừ (quận 5), các “quái xế” chặn cả một đoạn đường để một “quái xế” tổ chức biểu diễn đứng một chân trên yên xe lạng lách hoặc bốc đầu xe ... trong tiếng reo hò của “đồng đội”. </p>\r\n<p>\r\n<table style=\"width:262px;height:355px\" align=center>\r\n    <tbody>\r\n        <tr>\r\n            <td> <img alt=\"\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967204_7_1.jpg\"></td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n            <p align=center><font color=\"#808080\"> Một “quái xế” bị tai nạn trên đường Trần Hưng Đạo - TPHCM. Ảnh: Thành Đồng</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>5 giờ sáng, các “quái xế” từ ngã tư Nguyễn Văn Cừ - Nguyễn Trãi kéo ra đại lộ Võ Văn Kiệt định đua một phen ra trò trước khi giải tán nhưng đã bị các chiến sĩ CSGT truy đuổi. Sau khi đi nhiều vòng hy vọng tìm được một địa điểm để tỉ thí nhưng không thành, đến 6 giờ, hàng chục “quái xế” mới chịu ra về bằng những pha lạng </p>\r\n<p align=right><em>Theo Người Lao Động</em></p>\r\n						','Theo Thanh Niên','http://www.thanhnien.com.vn/news/Pages/201050/20101210100533.aspx',NULL,1,'uploads/news/12_2010/1291967203_7_0.jpg',NULL,'','','uploads/news/12_2010/1291967203_7_0.jpg~uploads/news/12_2010/1291967204_7_1.jpg','0',NULL,NULL,'0',NULL,'0',NULL,'','0',1,1291967202),(6,4,'Nguyn-Th-Huyn-lan-dau-khoe-ging-ht','Van-hoa','Nguyễn Thị Huyền lần đầu khoe giọng hát','Trong buổi họp báo tại Hà Nội, Đàm Vĩnh Hưng với vai trò nhà sản xuất bật mí về sự xuất hiện của Hoa hậu Việt Nam 2004 vào liveshow \\\'Thiên thần trong đêm\\\' ngày 19/3 của ca sĩ Dương Triệu Vũ. Nguyễn Thị Huyền sẽ song ca cùng giọng ca hải ngoại bài hát \\\'Tiếng mưa đêm\\\'.>> Hoa hậu Nguyễn Thị Huyền phá cách','<p class=\"Title\">Nguyễn Thị Huyền lần đầu khoe giọng hát</p>\n<p class=\"Lead\">Trong buổi họp báo tại Hà Nội, Đàm Vĩnh Hưng với vai trò nhà sản xuất bật mí về sự xuất hiện của Hoa hậu Việt Nam 2004 vào liveshow \'Thiên thần trong đêm\' ngày 19/3 của ca sĩ Dương Triệu Vũ. Nguyễn Thị Huyền sẽ song ca cùng giọng ca hải ngoại bài hát \'Tiếng mưa đêm\'.<br />\n&gt;&gt; <a href=\"/News/Hau-truong/2010/02/3B9CE212/\" style=\"\" class=\"Lead\">Hoa hậu Nguyễn Thị Huyền phá cách</a></p>\n<table width=\"1\" cellspacing=\"0\" cellpadding=\"3\" border=\"0\" align=\"center\">\n    <tbody>\n        <tr>\n            <td>\n            <p align=\"center\"><img width=\"500\" height=\"375\" border=\"1\" align=\"center\" alt=\"\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967206_8_0.jpg\" /></p>\n            </td>\n        </tr>\n        <tr>\n            <td class=\"Image\">Nguyễn Thị Huyền là một người em thân thiết của Mr Đàm. Cô xuất hiện trong liveshow của Dương Triệu Vũ với mục đích ủng hộ cho \"gà\" của người anh trai. Ảnh:<em> L.T.</em></td>\n        </tr>\n    </tbody>\n</table>\n<p class=\"Normal\">Theo lời Mr Đàm, anh đã ngạc nhiên khi được nghe giọng hát của Hoa hậu Việt Nam trong một lần cùng nhau trên chuyến bay từ Đà Lạt về Sài Gòn. “Huyền có chất giọng đặc biệt, khác xa so với nhiều chân dài khác”, Đàm Vĩnh Hưng nhận xét về năng khiếu ca hát của Nguyễn Thị Huyền. Anh đã thuyết phục người đẹp thể hiện giọng ca cùng Dương Triệu Vũ trong liveshow đầu tiên của nam ca sĩ tại Việt Nam. Cả hai sẽ tạo nên một câu chuyện tình lãng mạn giữa cậu sinh viên trẻ và cô giáo xinh đẹp trong bản song ca <em>Tiếng mưa đêm</em>. Trước đêm diễn, Nguyễn Thị Huyền và Dương Triệu Vũ đã lên Đà Lạt để quay clip ngắn giới thiệu về ca khúc. Đàm Vĩnh Hưng bật mí: “Huyền mặc áo dài màu cam, quần lụa trắng bước vào lớp giảng bài. Cô viết lên bảng dòng chữ về ngày, tháng, năm và quay lại nhìn học sinh. Vẻ đẹp rạng ngời của cô khiến cho các cậu học trò say mê, trong đó có Dương Triệu Vũ”. Câu chuyện tình sẽ tiếp tục được diễn ra trên sân khấu liveshow <em>Thiên thần trong đêm </em>vào ngày 19/3 khi Dương Triệu Vũ đến nhà cô giáo và cất tiếng hát trong cơn mưa.</p>\n<table width=\"1\" cellspacing=\"0\" cellpadding=\"3\" border=\"0\" align=\"center\">\n    <tbody>\n        <tr>\n            <td>\n            <p align=\"center\"><img width=\"450\" height=\"676\" border=\"1\" align=\"center\" alt=\"Dương Triệu Vũ được Đàm Vĩnh Hưng coi là người kế cận cho mình.\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967207_8_1.jpg\" /></p>\n            </td>\n        </tr>\n        <tr>\n            <td class=\"Image\">Dương Triệu Vũ được Đàm Vĩnh Hưng coi là người kế cận cho mình. Ảnh: <em>Lý Võ Phú Hưng.</em></td>\n        </tr>\n    </tbody>\n</table>\n<p class=\"Normal\">Tâm sự về lần đầu khoe giọng hát trước công chúng, Nguyễn Thị Huyền cho biết: “Tôi chỉ tham gia với mục đích ủng hộ cho Dương Triệu Vũ trên con đường chinh phục khán giả trong nước chứ không có ý định sẽ trở thành ca sĩ. Anh Đàm Vĩnh Hưng sau khi nghe xong bản thu karaoke của tôi từ 7 năm trước đã dành rất nhiều lời khen cho giọng hát của tôi. Anh ấy khuyên tôi nên lấn sân sang con đường ca hát nhưng tôi thấy mình không có đủ khả năng. Tôi chưa qua một trường lớp đào tạo nào, cũng không biết nhiều về thanh nhạc. Hiện tại, tôi chỉ muốn làm tốt công việc của mình tại VTV 4 mà thôi”. Cả Hoa hậu Việt Nam 2004 và ca sĩ Đàm Vĩnh Hưng đều đang chuẩn bị cho kế hoạch mở công ty truyền thông, tổ chức event, đào tạo ca sĩ tại Hà Nội trong tháng 5 tới.</p>\n<p class=\"Normal\">Ngoài sự ủng hộ của Nguyễn Thị Huyền trong liveshow, Dương Triệu Vũ còn nhận được rất nhiều sự hậu thuẫn từ các tên tuổi khác như Đàm Vĩnh Hưng, Lam Trường, Cẩm Ly, Quang Dũng, Hồng Ngọc, Lệ Quyên… và cả người anh trai Hoài Linh, chị gái Phương Trang cùng các danh hài Minh Nhí, Hữu Châu, Thanh Thúy, Hiếu Hiền. Toàn bộ ý tưởng về đêm diễn được Mr Đàm vạch ra và đạo diễn Trần Vy Mỹ là người dàn dựng. Đứng ở vai trò của nhà sản xuất, nam ca sĩ <em>Hạnh phúc cuối</em> thể hiện sự quyết tâm: \"Tôi biết rõ thực lực \'gà\' của mình như thế nào và muốn chiến thắng ngay trong trận đánh đầu tiên. Từng đi diễn nhiều, lại có không ít kinh nghiệm, tôi biết tính toán chính xác thời điểm và tạo mọi điều kiện thuận lợi cho người kế cận của mình gặt hái được thành công\".</p>\n<table width=\"1\" cellspacing=\"0\" cellpadding=\"3\" border=\"0\" align=\"center\">\n    <tbody>\n        <tr>\n            <td>\n            <p align=\"center\"><img width=\"450\" height=\"676\" border=\"1\" align=\"center\" alt=\"\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967208_8_2.jpg\" /></p>\n            </td>\n        </tr>\n        <tr>\n            <td class=\"Image\">Tuy là em trai của danh hài Hoài Linh nhưng Dương Triệu Vũ lại không muốn nhờ anh trai mình nâng đỡ trong sự nghiệp ca hát. Ảnh: <em>Lý Võ Phú Hưng.</em></td>\n        </tr>\n    </tbody>\n</table>\n<p class=\"Normal\">Vốn là con nuôi trong gia đình Hoài Linh, Mr Đàm đã phát hiện ra tố chất ca sĩ và \"huấn luyện\" Dương Triệu Vũ từ năm 16 tuổi. Anh đánh giá cao khả năng hát giọng giả thanh của cậu học trò bởi đó là điều anh không làm được, \"Tôi chỉ giỏi gào thét chứ không chuyển giọng ngọt như cậu ấy\".</p>\n<p class=\"Normal\">Tâm sự trong buổi họp báo tại Hà Nội, Triệu Vũ cho biết, khi được báo chí gọi là người kế cận Mr Đàm anh cảm thấy rất áp lực. Nhưng đây cũng là lời động viên lớn giúp anh chứng tỏ được khả năng đa dạng của mình về phong cách âm nhạc. Anh không muốn trở thành Đàm Vĩnh Hưng 2 mà chỉ mong sẽ đạt được một chút thành công thầy của mình đã làm được trong sự nghiệp. Trở về Việt Nam phát triển sự nghiệp, cậu em trai danh hài Hoài Linh mong muốn nhận được nhiều sự ủng hộ của khán giả.&#160;</p>\n<p align=\"right\" class=\"Normal\"><strong>Lương Trần</strong></p>\n<p><img width=\"109\" height=\"33\" border=\"0\" align=\"right\" alt=\"\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967209_8_3.gif\" /></p>','Theo Ngôi Sao','http://ngoisao.net/News/Hau-truong/2010/03/3B9CE489/',NULL,1,'uploads/news/12_2010/1291967206_8_0.jpg','uploads/news/12_2010/1291967206_8_0_thumb_thumb.jpg','','','uploads/news/12_2010/1291967206_8_0.jpg~uploads/news/12_2010/1291967207_8_1.jpg~uploads/news/12_2010/1291967208_8_2.jpg~uploads/news/12_2010/1291967209_8_3.gif','1',1291968929,1292004929,'1',1291968929,'1',1291968929,'1','1',0,1291967204),(7,19,'My-nhan-Nam-Phi-dien-bikini-quyen-ru','Camera-can-canh','Mỹ nhân Nam Phi diện bikini quyến rũ','Candice Swanepoel đã xuất hiện trong bộ sưu tập áo tắm hai mảnh mới nhất của Victoria\\\'s Secret dành cho năm 2011.','\r\n        <a name=\"top\"></a>\r\n        <h1 class=\"pTitle\">Mỹ nhân Nam Phi diện bikini quyến rũ</h1><p class=\"pHead\">Candice Swanepoel đã xuất hiện trong bộ sưu tập áo tắm hai mảnh mới nhất của Victoria\'s Secret dành cho năm 2011.</p><p class=\"pBody\">&gt;&gt; <a class=\"lnk02\" href=\"http://www.zing.vn/news/nguoi-dep/hut-hon-truoc-nhan-sac-cua-candice-swanepoel/a99419.html\">Hút hồn trước nhan sắc của Candice Swanepoel</a><br /> &gt;&gt;<a class=\"lnk02\" href=\"http://www.zing.vn/news/nguoi-dep/hut-hon-truoc-nhan-sac-cua-candice-swanepoel/a99419.html\">Candice Swanepoel n</a><a class=\"lnk02\" href=\"http://www.zing.vn/news/nguoi-dep/candice-swanepoel-nong-bong-cung-noi-y/a98715.html\">óng bỏng cùng nội y</a><br /><br /> Gia nhập làng người mẫu từ năm 15 tuổi, tên tuổi của cô gái đến từ một thị trấn nhỏ bên sông Mooi thơ mộng nhanh chóng được chú ý bởi gương mặt gợi cảm cùng đường cong quyến rũ. Dù đã từng xuất hiện trong rất nhiều bộ ảnh quảng cáo của Nike, Diesel, Guess, Versus... nhưng cho tới giờ, Victoria\'s Secret vẫn là nhãn hiệu gắn liền với tên tuổi của người đẹp sinh năm 1988 này.</p><table cellspacing=\"1\" cellpadding=\"1\" width=\"200\" align=\"center\" border=\"0\"><tbody><tr><td><img  hspace=\"0\" align=\"middle\" border=\"0\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967210_9_0.jpg\" class=\"oImage\" onclick=\"return showImage(this.src)\" alt=\"Mỹ nhân Nam Phi diện bikini quyến rũ\" /></td></tr><tr><td><img  hspace=\"0\" align=\"middle\" border=\"0\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967210_9_1.jpg\" class=\"oImage\" onclick=\"return showImage(this.src)\" alt=\"Mỹ nhân Nam Phi diện bikini quyến rũ\" /></td></tr><tr><td><img  hspace=\"0\" align=\"middle\" border=\"0\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967211_9_2.jpg\" class=\"oImage\" onclick=\"return showImage(this.src)\" alt=\"Mỹ nhân Nam Phi diện bikini quyến rũ\" /></td></tr><tr><td><img  hspace=\"0\" align=\"middle\" border=\"0\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967211_9_3.jpg\" class=\"oImage\" onclick=\"return showImage(this.src)\" alt=\"Mỹ nhân Nam Phi diện bikini quyến rũ\" /></td></tr><tr><td><img  hspace=\"0\" align=\"middle\" border=\"0\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967211_9_4.jpg\" class=\"oImage\" onclick=\"return showImage(this.src)\" alt=\"Mỹ nhân Nam Phi diện bikini quyến rũ\" /></td></tr><tr><td><img  hspace=\"0\" align=\"middle\" border=\"0\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967212_9_5.jpg\" class=\"oImage\" onclick=\"return showImage(this.src)\" alt=\"Mỹ nhân Nam Phi diện bikini quyến rũ\" /></td></tr><tr><td><img  hspace=\"0\" align=\"middle\" border=\"0\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967212_9_6.jpg\" class=\"oImage\" onclick=\"return showImage(this.src)\" alt=\"Mỹ nhân Nam Phi diện bikini quyến rũ\" /></td></tr><tr><td><img  hspace=\"0\" align=\"middle\" border=\"0\" src=\"http://crawl.smartdzine.vn/uploads/news/12_2010/1291967213_9_7.jpg\" class=\"oImage\" onclick=\"return showImage(this.src)\" alt=\"Mỹ nhân Nam Phi diện bikini quyến rũ